// Extract and validate sensor data
const sensorData = $input.first().json;

// Validate required fields
if (!sensorData.machineId || !sensorData.timestamp) {
  throw new Error('Missing required fields: machineId or timestamp');
}

// Extract sensor readings
const readings = {
  machineId: sensorData.machineId,
  timestamp: new Date(sensorData.timestamp).toISOString(),
  temperature: parseFloat(sensorData.temperature) || 0,
  pressure: parseFloat(sensorData.pressure) || 0,
  vibration: parseFloat(sensorData.vibration) || 0,
  humidity: parseFloat(sensorData.humidity) || 0,
  rpm: parseFloat(sensorData.rpm) || 0
};

// Add derived metrics
readings.temperaturePressureRatio = readings.temperature / (readings.pressure || 1);
readings.vibrationRpmRatio = readings.vibration / (readings.rpm || 1);

return { json: readings };

// Isolation Forest Algorithm Implementation
class IsolationForest {
  constructor(nTrees = 100, subsampleSize = 256) {
    this.nTrees = nTrees;
    this.subsampleSize = subsampleSize;
    this.trees = [];
  }

  fit(data) {
    this.trees = [];
    for (let i = 0; i < this.nTrees; i++) {
      const sample = this.subsample(data);
      const tree = this.buildTree(sample, 0, Math.ceil(Math.log2(this.subsampleSize)));
      this.trees.push(tree);
    }
  }

  subsample(data) {
    const shuffled = [...data].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, Math.min(this.subsampleSize, data.length));
  }

  buildTree(data, currentDepth, maxDepth) {
    if (currentDepth >= maxDepth || data.length <= 1) {
      return { type: 'leaf', size: data.length };
    }

    const features = Object.keys(data[0]);
    const selectedFeature = features[Math.floor(Math.random() * features.length)];
    const values = data.map(d => d[selectedFeature]).filter(v => v !== undefined && v !== null);

    if (values.length === 0) {
      return { type: 'leaf', size: data.length };
    }

    const minVal = Math.min(...values);
    const maxVal = Math.max(...values);

    if (minVal === maxVal) {
      return { type: 'leaf', size: data.length };
    }

    const splitValue = minVal + Math.random() * (maxVal - minVal);

    const leftData = data.filter(d => d[selectedFeature] < splitValue);
    const rightData = data.filter(d => d[selectedFeature] >= splitValue);

    return {
      type: 'node',
      feature: selectedFeature,
      splitValue: splitValue,
      left: this.buildTree(leftData, currentDepth + 1, maxDepth),
      right: this.buildTree(rightData, currentDepth + 1, maxDepth)
    };
  }

  pathLength(point, tree, currentDepth = 0) {
    if (tree.type === 'leaf') {
      return currentDepth + this.c(tree.size);
    }

    if (point[tree.feature] < tree.splitValue) {
      return this.pathLength(point, tree.left, currentDepth + 1);
    } else {
      return this.pathLength(point, tree.right, currentDepth + 1);
    }
  }

  c(n) {
    if (n <= 1) return 0;
    return 2 * (Math.log(n - 1) + 0.5772156649) - (2 * (n - 1) / n);
  }

  anomalyScore(point) {
    const avgPathLength = this.trees.reduce((sum, tree) => {
      return sum + this.pathLength(point, tree);
    }, 0) / this.trees.length;

    return Math.pow(2, -avgPathLength / this.c(this.subsampleSize));
  }
}

// Statistical Anomaly Detection
function calculateZScore(value, mean, stdDev) {
  return stdDev === 0 ? 0 : Math.abs((value - mean) / stdDev);
}

function calculateStatistics(data, field) {
  const values = data.map(d => d[field]).filter(v => v !== undefined && v !== null);
  if (values.length === 0) return { mean: 0, stdDev: 0, min: 0, max: 0 };

  const mean = values.reduce((a, b) => a + b, 0) / values.length;
  const variance = values.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / values.length;
  const stdDev = Math.sqrt(variance);

  return {
    mean: mean,
    stdDev: stdDev,
    min: Math.min(...values),
    max: Math.max(...values)
  };
}

// Main anomaly detection logic
const currentReading = $input.first().json;
const historicalData = $input.last().json;

if (!historicalData || historicalData.length < 10) {
  return {
    json: {
      ...currentReading,
      anomalyScore: 0,
      isAnomaly: false,
      anomalyType: 'insufficient_data',
      confidence: 0,
      details: 'Insufficient historical data for anomaly detection'
    }
  };
}

// Prepare data for analysis
const features = ['temperature', 'pressure', 'vibration', 'humidity', 'rpm', 'temp_pressure_ratio', 'vibration_rpm_ratio'];
const cleanHistoricalData = historicalData.map(row => {
  const cleanRow = {};
  features.forEach(feature => {
    cleanRow[feature] = parseFloat(row[feature]) || 0;
  });
  return cleanRow;
});

const currentData = {};
features.forEach(feature => {
  currentData[feature] = parseFloat(currentReading[feature]) || 0;
});

// Statistical Analysis
const statisticalAnomalies = [];
let maxZScore = 0;

features.forEach(feature => {
  const stats = calculateStatistics(cleanHistoricalData, feature);
  const zScore = calculateZScore(currentData[feature], stats.mean, stats.stdDev);

  if (zScore > 2) {
    statisticalAnomalies.push({
      feature: feature,
      value: currentData[feature],
      zScore: zScore,
      mean: stats.mean,
      stdDev: stats.stdDev
    });
  }

  maxZScore = Math.max(maxZScore, zScore);
});

// Isolation Forest Analysis
const isolationForest = new IsolationForest(50, Math.min(128, cleanHistoricalData.length));
isolationForest.fit(cleanHistoricalData);
const isolationScore = isolationForest.anomalyScore(currentData);

// Combined anomaly detection
const statisticalAnomalyScore = Math.min(maxZScore / 3, 1); // Normalize to 0-1
const combinedScore = (isolationScore * 0.6) + (statisticalAnomalyScore * 0.4);

// Determine anomaly threshold and severity
const anomalyThreshold = 0.6;
const isAnomaly = combinedScore > anomalyThreshold;

let severity = 'normal';
let alertLevel = 'info';

if (combinedScore > 0.8) {
  severity = 'critical';
  alertLevel = 'critical';
} else if (combinedScore > 0.7) {
  severity = 'high';
  alertLevel = 'warning';
} else if (combinedScore > 0.6) {
  severity = 'medium';
  alertLevel = 'warning';
}

// Identify primary anomaly causes
const anomalyCauses = statisticalAnomalies
  .sort((a, b) => b.zScore - a.zScore)
  .slice(0, 3)
  .map(a => `${a.feature}: ${a.value.toFixed(2)} (Z-score: ${a.zScore.toFixed(2)})`);

const result = {
  ...currentReading,
  anomalyScore: parseFloat(combinedScore.toFixed(4)),
  isolationScore: parseFloat(isolationScore.toFixed(4)),
  statisticalScore: parseFloat(statisticalAnomalyScore.toFixed(4)),
  isAnomaly: isAnomaly,
  severity: severity,
  alertLevel: alertLevel,
  confidence: parseFloat((combinedScore * 100).toFixed(2)),
  anomalyCauses: anomalyCauses,
  statisticalAnomalies: statisticalAnomalies.length,
  detectionTimestamp: new Date().toISOString(),
  analysisDetails: {
    historicalDataPoints: cleanHistoricalData.length,
    maxZScore: parseFloat(maxZScore.toFixed(2)),
    threshold: anomalyThreshold
  }
};

return { json: result };

