// Send real-time data to monitoring dashboard
const data = $input.first().json;

// Prepare metrics for Prometheus/InfluxDB
const metrics = {
  machine_id: data.machineId,
  timestamp: new Date(data.timestamp).getTime(),
  temperature: data.temperature,
  pressure: data.pressure,
  vibration: data.vibration,
  humidity: data.humidity,
  rpm: data.rpm,
  anomaly_score: data.anomalyScore,
  is_anomaly: data.isAnomaly ? 1 : 0,
  severity_level: {
    'normal': 0,
    'medium': 1,
    'high': 2,
    'critical': 3
  }[data.severity] || 0
};

return { json: metrics };

// Check if maintenance is needed based on anomaly patterns
const results = $input.first().json;

if (!results || results.length === 0) {
  return { json: { maintenanceNeeded: false, message: 'No maintenance required' } };
}

const maintenanceAlerts = results.map(machine => {
  let priority = 'medium';
  let recommendation = '';

  if (machine.anomaly_count > 10 || machine.max_score > 0.9) {
    priority = 'urgent';
    recommendation = 'Immediate maintenance required - multiple critical anomalies detected';
  } else if (machine.anomaly_count > 6 || machine.avg_score > 0.7) {
    priority = 'high';
    recommendation = 'Schedule maintenance within 24 hours';
  } else {
    recommendation = 'Schedule routine maintenance check';
  }

  return {
    machineId: machine.machine_id,
    anomalyCount: machine.anomaly_count,
    avgScore: parseFloat(machine.avg_score).toFixed(3),
    maxScore: parseFloat(machine.max_score).toFixed(3),
    priority: priority,
    recommendation: recommendation
  };
});

return {
  json: {
    maintenanceNeeded: true,
    alertCount: maintenanceAlerts.length,
    machines: maintenanceAlerts,
    generatedAt: new Date().toISOString()
  }
};

